package $packageName$;

import java.util.*;
import com.microsoft.azure.functions.annotation.*;
import com.microsoft.azure.functions.*;

/**
 * Azure Functions with HTTP trigger.
 */
public class $className$ {
    /**
     * This function will listen at HTTP endpoint "/api/$functionName$". Two approaches to invoke it using "curl" command in bash:
     *   1. curl -d "Http Body" {your host}/api/$functionName$
     *   2. curl {your host}/api/$functionName$?name=HTTP%20Query
     */
    @FunctionName("$functionName$")
    public HttpResponseMessage<String> httpHandler(
        @HttpTrigger(name = "req", methods = { "get", "post" }, authLevel = AuthorizationLevel.$authLevel$) HttpRequestMessage<Optional<String>> request,
        final ExecutionContext context
    ) {
        context.getLogger().info("Java HTTP trigger processed a request.");

        // Parse query parameters
        String query = request.getQueryParameters().get("name");
        String name = request.getBody().orElse(query);

        if (name == null) {
            return request.createResponse(400, "Please pass a name on the query string or in the request body");
        } else {
            return request.createResponse(200, "Hello, " + name);
        }
    }
}

